/**
 *
 * @namespace faker.molbiol
 */
var Molbiol = function (faker) {
  var self = this;

  /**
   * color
   *
   * @method faker.molbiol.color
   *
  self.color = function() {
      return faker.random.arrayElement(faker.definitions.molbiol.color);
  };

  /**
   * department
   *
   * @method faker.molbiol.department
   * @param {number} max
   * @param {number} fixedAmount
   *
  self.department = function(max, fixedAmount) {
      return faker.random.arrayElement(faker.definitions.molbiol.department);
  };

  /**
   * productName
   *
   * @method faker.molbiol.productName
   */
  self.productName = function() {
      return faker.molbiol.productAdjective() + " " +
              faker.molbiol.productMaterial() + " " +
              faker.molbiol.product();
  };

  /**
   * price
   *
   * @method faker.molbiol.price
   * @param {number} min
   * @param {number} max
   * @param {number} dec
   * @param {string} symbol
   */
  self.price = function(min, max, dec, symbol) {
      min = min || 0;
      max = max || 1000;
      dec = dec || 2;
      symbol = symbol || '';

      if(min < 0 || max < 0) {
          return symbol + 0.00;
      }

      var randValue = faker.random.number({ max: max, min: min });

      return symbol + (Math.round(randValue * Math.pow(10, dec)) / Math.pow(10, dec)).toFixed(dec);
  };

  /*
  self.categories = function(num) {
      var categories = [];

      do {
          var category = faker.random.arrayElement(faker.definitions.molbiol.department);
          if(categories.indexOf(category) === -1) {
              categories.push(category);
          }
      } while(categories.length < num);

      return categories;
  };

  */
  /*
  self.mergeCategories = function(categories) {
      var separator = faker.definitions.separator || " &";
      // TODO: find undefined here
      categories = categories || faker.definitions.molbiol.categories;
      var commaSeparated = categories.slice(0, -1).join(', ');

      return [commaSeparated, categories[categories.length - 1]].join(separator + " ");
  };
  */

  /**
   * productAdjective
   *
   * @method faker.molbiol.productAdjective
   */
  self.productAdjective = function() {
      return faker.random.arrayElement(faker.definitions.molbiol.product_name.adjective);
  };

  /**
   * productMaterial
   *
   * @method faker.molbiol.productMaterial
   */
  self.productMaterial = function() {
      return faker.random.arrayElement(faker.definitions.molbiol.product_name.material);
  };

  /**
   * product
   *
   * @method faker.molbiol.product
   */
  self.product = function() {
      return faker.random.arrayElement(faker.definitions.molbiol.product_name.product);
  };

  return self;
};

module['exports'] = Molbiol;



